--
-- Table structure for table `news_post_comments`
--

DROP TABLE IF EXISTS `news_post_comments`;
CREATE TABLE IF NOT EXISTS `news_post_comments` (
  `id` int UNSIGNED NOT NULL AUTO_INCREMENT,
  `news_post_id` int UNSIGNED NOT NULL,
  `parent_id` int UNSIGNED DEFAULT NULL,
  `guest_name` varchar(120) COLLATE utf8mb4_general_ci NOT NULL,
  `guest_email` varchar(150) COLLATE utf8mb4_general_ci NOT NULL,
  `comment` text COLLATE utf8mb4_general_ci NOT NULL,
  `is_admin_reply` tinyint NOT NULL DEFAULT '0' COMMENT '1 = admin reply',
  `status` tinyint NOT NULL DEFAULT '0' COMMENT '0 = pending, 1 = approved',
  `recaptcha_score` decimal(3,2) DEFAULT NULL,
  `ip_address` varchar(45) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `user_agent` text COLLATE utf8mb4_general_ci,
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  `deleted_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `news_post_id` (`news_post_id`),
